	// 	});
  // }
  


}



  return (
    <Container className="loginForm" component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
         Verify
        </Typography>
        <form className={classes.form} noValidate>
          <TextField
            type="password"
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="email"
            label="your secrate key"
            name="email"
            autoComplete="email"
            autoFocus
          />
         
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            Sign In
          </Button>

         


        </form>
      </div>
    
    </Container>
  );
}
