                    </FormControl>
                  </GridItem>

                  <GridItem xs={12} sm={12} md={4}>
                    <FormControl
                      fullWidth
                      required="true"
                      style={{ marginTop: 28 }}
                      className={classes.formControl}
                    >
                      <InputLabel htmlFor="age-native-simple">Diet </InputLabel>
                      <Select
                        native
                        value={this.state.dietType}
                        onChange={this.handleChange}
                        inputProps={{
                          name: "dietType",
                          id: "dietType",
                        }}
                      >
                        <option aria-label="None" value="" />

                        {this.state.dietTypeList.map((e, key) => {
                          return (
                            <option key={key} value={e.dietType}>
                              {e.dietType}
                            </option>
                          );
                        })}
                      </Select>
                    </FormControl>
                  </GridItem>

                  <GridItem xs={12} sm={12} md={4}>
                    <FormControl
                      fullWidth
                      required="true"
                      style={{ marginTop: 28 }}
                      className={classes.formControl}
                    >
                      <InputLabel htmlFor="age-native-simple">
                        Availablity{" "}
                      </InputLabel>
                      <Select
                        native
                        value={this.state.availablity}
                        onChange={this.handleChange}
                        inputProps={{
                          name: "availablity",
                          id: "availablity",
                        }}
                      >
                        <option aria-label="None" value="" />

                        {this.state.availabiltyList.map((e, key) => {
                          return (
                            <option key={key} value={e.availablity}>
                              {e.availablity}
                            </option>
                          );
                        })}
                      </Select>
                    </FormControl>
                  </GridItem>
                </GridContainer>




                <GridContainer>
              

                    
                    <GridItem xs={12} sm={12} md={4}>
                      <CustomInput
                        labelText="Weight"
                        type="text"
                        id="weight"
                        name="weight"
                        value={this.state.weight}
                        onChange={this.handleChange}
                        formControlProps={{
                          fullWidth: true,
                        }}
                      />
                    </GridItem>

                    <GridItem xs={12} sm={12} md={4}>
                      <CustomInput
                        labelText="Length"
                        type="text"
                        id="length"
                        name="length"
                        value={this.state.length}
                        onChange={this.handleChange}
                        formControlProps={{
                          fullWidth: true,
                        }}
                      />
                    </GridItem>

                    <GridItem xs={12} sm={12} md={4}>
                      {/* <CustomInput
                        labelText="DOB"
                        type="text"
                        id="DOB"
                        name="DOB"
                        selected={this.state.DOB}
                        onChange={this.changeDate}
                        formControlProps={{
                          fullWidth: true,
                        }}
                      /> */}

                      {/* <MuiPickersUtilsProvider utils={DateFnsUtils}>
      <Grid container justify="space-around">
        <KeyboardDatePicker
          disableToolbar
          variant="inline"
          format="MM/dd/yyyy"
          margin="normal"
          id="date-picker-inline"
          label="Date picker inline"
          value={this.state.DOB}
          onChange={this.changeDate}
          KeyboardButtonProps={{
            'aria-label': 'change date',
          }}
        />
       
       
      </Grid>
    </MuiPickersUtilsProvider> */}
{/* className={classes.container} */}
{/* selected={this.state.DOB} */}
     <form fullWidth  style={{marginTop:28}}  className={classes.container} noValidate>
      <TextField
       fullWidth
        id="DOB"
          name="DOB"
        label="DOB"
        type="date"
         defaultValue={this.state.DOB}    //"2020-01-30"
      selected={this.state.DOB}      
      onChange={this.changeDate}
        className={classes.textField}
        InputLabelProps={{
          shrink: true,
        }}
      />
    </form>


                    </GridItem>
                 
                </GridContainer>



            <GridContainer>
               
              <GridItem xs={12} sm={12} md={12}>
                <CustomInput
                  labelText="Tag coma seperated"
                  type="text"
                  id="tag"
                  name="tag"
                  value={this.state.weight}
                  onChange={this.handleChange}
                  formControlProps={{
                    fullWidth: true,
                  }}
                />
              </GridItem>

            
           
           
          </GridContainer>



                <GridContainer>
                  <GridItem  xs={12} sm={12} md={2}>
                    {/* <TextField
                      multiple
                      required= {true}
                      error = {true}
                      label="Item Image"
                      id="itemImage"
                      name="itemImage"
                      type="file"
                      margin="normal"
                      InputLabelProps={{
                        shrink: true,
                      }}
                      variant="outlined"
                      onChange={this.changeItemImage}
                    /> */}
 
                 <input
               
                type="file"
                required = "true"
                error = {this.state.image_error}
								src=""
								// alt="Image"
								// width="300"
								// height="300"
								id="itemImage"
								name="itemImage"
								accept="Image"
								// placeholder="Your Picture.."
								onChange={this.changeItemImage}
								multiple 
							/> 

                  
                  </GridItem>

                  <GridItem xs={12} sm={12} md={2}>
                    <Card>
                      <CardActionArea>
                        {/* <CardMedia
                          component="img"
                          alt="contemplative reptil"
                          height="150"
                          width="200"
                          title="contemplative reptil"
                         
                          src={this.state.upload}
                        /> */}
                        {(this.fileArray || []).map(url => (
                        <img src={url} alt="..." />
                    ))}
                      </CardActionArea>
                    </Card>
                  </GridItem>
                </GridContainer>
              </CardBody>

              <CardFooter>
                <Button onClick={this.addItemeHandler} color="primary">
                  Save
                </Button>
              </CardFooter>
            </Card>
          </GridItem>



        </GridContainer>
      </div>
    );
  } 
}

ItemEntry.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ItemEntry);
